
-- Insert into Profesor
SELECT id_usuario, 'EMP' || CAST(id_usuario AS TEXT), 'IIA'
FROM escom_aliz.usuario u
WHERE rol = 'PROFESOR'
AND NOT EXISTS (SELECT 1 FROM escom_aliz.profesor p WHERE p.id_profesor = u.id_usuario);

